import React, { useState } from "react";

const CoverLetterForm: React.FC = () => {
  const [resume, setResume] = useState<File | null>(null);
  const [jobDescription, setJobDescription] = useState("");
  const [notes, setNotes] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!resume || !jobDescription.trim()) return;

    const formData = new FormData();
    formData.append("resume", resume);
    formData.append("jobDescription", jobDescription);
    formData.append("notes", notes);

    setLoading(true);
    try {
      const response = await fetch("https://resume-reviewer-production.up.railway.app/api/cover-letter", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setOutput(data.coverLetter || "No output received.");
    } catch (err) {
      console.error(err);
      setOutput("Error generating cover letter.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Cover Letter Creator</h2>

      <label>Upload Resume (PDF/DOC/DOCX)</label><br />
      <input type="file" accept=".pdf,.doc,.docx" onChange={e => e.target.files && setResume(e.target.files[0])} /><br /><br />

      <label>Job Description</label><br />
      <textarea value={jobDescription} onChange={e => setJobDescription(e.target.value)} rows={6} style={{ width: "100%" }} /><br /><br />

      <label>Optional Notes</label><br />
      <textarea value={notes} onChange={e => setNotes(e.target.value)} rows={4} style={{ width: "100%" }} /><br /><br />

      <button type="submit" disabled={loading || !resume || !jobDescription}>
        {loading ? "Generating..." : "Generate Cover Letter"}
      </button>

      {output && (
        <div style={{ marginTop: "2rem" }}>
          <h3>AI-Generated Cover Letter</h3>
          <pre>{output}</pre>
        </div>
      )}
    </form>
  );
};

export default CoverLetterForm;
