import React, { useEffect, useState } from "react";

interface ResumeFeedback {
  summary: string;
  strengths: string[];
  areas_for_improvement: string[];
  recommendations: string[];
}

const FeedbackDisplay = () => {
  const [feedback, setFeedback] = useState<ResumeFeedback | null>(null);

  useEffect(() => {
    const stored = localStorage.getItem("feedback");
    if (stored) {
      setFeedback(JSON.parse(stored));
    }
  }, []);

  if (!feedback) return null;

  return (
    <div>
      <h2>AI Resume Feedback</h2>
      <p><strong>Summary:</strong> {feedback.summary}</p>

      <h3>Strengths</h3>
      <ul>
        {feedback.strengths.map((item, i) => <li key={i}>{item}</li>)}
      </ul>

      <h3>Areas for Improvement</h3>
      <ul>
        {feedback.areas_for_improvement.map((item, i) => <li key={i}>{item}</li>)}
      </ul>

      <h3>Recommendations</h3>
      <ul>
        {feedback.recommendations.map((item, i) => <li key={i}>{item}</li>)}
      </ul>
    </div>
  );
};

export default FeedbackDisplay;
